do local WindUI=loadstring(game:HttpGet("https://github.com/Footagesus/WindUI/releases/latest/download/main.lua"))();local ReplicatedStorage=game:GetService("ReplicatedStorage");local Players=game:GetService("Players");local LocalPlayer=Players.LocalPlayer;local RunService=game:GetService("RunService");local Window=WindUI:CreateWindow({Title="EyeHub | Grow A Garden",Icon="leaf",Author="by EyeHub",Folder="EyeHub",Size=UDim2.fromOffset(580,460),Theme="Dark",SideBarWidth=160,User={Enabled=false},Config={Enabled=false}});local Tabs={Main=Window:Tab({Title="Main",Icon="home"}),Farm=Window:Tab({Title="Farm",Icon="leaf"}),Merchant=Window:Tab({Title="Merchant",Icon="coins"}),Shop=Window:Tab({Title="Shop",Icon="shopping-cart"}),Visual=Window:Tab({Title="Visual",Icon="eye"}),Settings=Window:Tab({Title="Settings",Icon="settings"})};do Tabs.Main:Paragraph({Title="What's New?",Desc="Rework Boost Speed\nRework Teleport Sky\nJoin discord EyeHub"});Tabs.Main:Button({Title="Copy Discord Invite",Desc="Join our Discord server",Callback=function() setclipboard("https://discord.gg/9krxH9g4MH");WindUI:Notify({Title="Copied!",Content="Discord link copied to clipboard.",Duration=5});end});end do local Plant_RE=ReplicatedStorage:FindFirstChild("GameEvents") and ReplicatedStorage.GameEvents:FindFirstChild("Plant_RE") ;if Plant_RE then local seedNames={"Carrot","Strawberry","Blueberry","Rose","Orange Tulip","Stonebite","Tomato","Daffodil","Cauliflower","Raspberry","Foxglove","Peace Lily","Corn","Paradise Petal","Horsetail","Watermelon","Pumpkin","Avocado","Green Apple","Banana","Lilac","Aloe Vera","Bamboo","Rafflesia","Lingonberry","Horned Dinoshroom","Boneboo","Peach","Pineapple","Guanabana","Amber Spine","Coconut","Cactus","Dragon Fruit","Mango","Kiwi","Bell Pepper","Prickly Pear","Pink Lily","Purple Dahlia","Firefly Fern","Grape","Loquat","Mushroom","Pepper","Cacao","Feijoa","Pitcher Plant","Grand","Sunflower","Fossilight","Beanstalk","Ember Lily","Sugar Apple","Burning Bud","Giant Pinecone","Bone Blossom"};local AutoPlantEnabled=false;local SelectedSeed=seedNames[1];Tabs.Farm:Dropdown({Title="Select Seed to Plant",Values=seedNames,Multi=false,Default=1,Callback=function(Value) SelectedSeed=Value;end});Tabs.Farm:Toggle({Title="Auto Plant Below Feet",Default=false,Callback=function(Value) AutoPlantEnabled=Value;end});task.spawn(function() while task.wait(1) do if (AutoPlantEnabled and SelectedSeed) then local char=LocalPlayer.Character;if (char and char:FindFirstChild("HumanoidRootPart")) then local pos=char.HumanoidRootPart.Position;Plant_RE:FireServer(Vector3.new(pos.X,0.1355,pos.Z),SelectedSeed);end end end end);end local Farms=workspace:FindFirstChild("Farm");if Farms then local MyFarm;for _,Farm in Farms:GetChildren() do if (Farm:FindFirstChild("Important") and Farm.Important:FindFirstChild("Data")) then local Data=Farm.Important.Data;if (Data:FindFirstChild("Owner") and (Data.Owner.Value==LocalPlayer.Name)) then MyFarm=Farm;break;end end end if (MyFarm and MyFarm:FindFirstChild("Important") and MyFarm.Important:FindFirstChild("Plants_Physical")) then local AutoHarvestEnabled=false;RunService.Heartbeat:Connect(function() if (AutoHarvestEnabled and LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart")) then local Position=LocalPlayer.Character.HumanoidRootPart.Position;for _,Plant in MyFarm.Important.Plants_Physical:GetDescendants() do if Plant:IsA("Model") then local Prompt=Plant:FindFirstChild("ProximityPrompt",true);if (Prompt and Prompt.Enabled) then local PlantPos=Plant:GetPivot().Position;if ((PlantPos-Position).Magnitude<=15) then fireproximityprompt(Prompt);end end end end end end);Tabs.Farm:Toggle({Title="Auto-Harvest",Default=false,Callback=function(Value) AutoHarvestEnabled=Value;end});end end end do local MerchantEvent=ReplicatedStorage:FindFirstChild("GameEvents") and ReplicatedStorage.GameEvents:FindFirstChild("BuyTravelingMerchantShopStock") ;if MerchantEvent then local merchantItems={"Cauliflower","Rafflesia","Green Apple","Avocado","Banana","Pineapple","Kiwi","Bell Pepper","Prickly Pear","Loquat","Feijoa","Pitcher","All"};local AutoBuyEnabled=false;local SelectedItem=merchantItems[1];Tabs.Merchant:Dropdown({Title="Select Seed Merchant",Values=merchantItems,Multi=false,Default=1,Callback=function(Value) SelectedItem=Value;end});Tabs.Merchant:Toggle({Title="Buy Seed Merchant",Default=false,Callback=function(Value) AutoBuyEnabled=Value;end});task.spawn(function() while task.wait(1) do if (AutoBuyEnabled and SelectedItem) then if (SelectedItem=="All") then for _,item in ipairs(merchantItems) do if (item~="All") then MerchantEvent:FireServer(item);task.wait(0.1);end end else MerchantEvent:FireServer(SelectedItem);end end end end);end end do local BuyEvent=ReplicatedStorage:FindFirstChild("GameEvents");local BuyEggEvent=BuyEvent and BuyEvent:FindFirstChild("BuyEggStock") ;BuyEvent=BuyEvent and BuyEvent:FindFirstChild("BuySeedStock") ;if (BuyEvent or BuyEggEvent) then local seeds={"Carrot","Strawberry","Blueberry","Orange Tulip","Tomato","Corn","Daffodil","Watermelon","Pumpkin","Apple","Bamboo","Coconut","Cactus","Dragon Fruit","Mango","Grape","Mushroom","Pepper","Cacao","Beanstalk","Ember Lily","Sugar Apple","Burning Bud","Giant Pinecone"};local eggs={"Common Egg","Common Summer Egg","Rare Summer Egg","Mythical Egg","Paradise Egg","Bug Egg"};local SelectedSeedToBuy=seeds[1];local SelectedEggToBuy=eggs[1];local AutoBuySeeds=false;local AutoBuyEggs=false;if BuyEvent then Tabs.Shop:Dropdown({Title="Select Seed to Buy",Values=seeds,Multi=false,Default=1,Callback=function(Value) SelectedSeedToBuy=Value;end});Tabs.Shop:Toggle({Title="Auto Buy Seeds",Default=false,Callback=function(Value) AutoBuySeeds=Value;end});end if BuyEggEvent then Tabs.Shop:Dropdown({Title="Select Egg to Buy",Values=eggs,Multi=false,Default=1,Callback=function(Value) SelectedEggToBuy=Value;end});Tabs.Shop:Toggle({Title="Auto Buy Eggs",Default=false,Callback=function(Value) AutoBuyEggs=Value;end});end task.spawn(function() while task.wait(0.5) do if (BuyEvent and AutoBuySeeds) then BuyEvent:FireServer(SelectedSeedToBuy);end if (BuyEggEvent and AutoBuyEggs) then BuyEggEvent:FireServer(SelectedEggToBuy);end end end);end end do Tabs.Visual:Button({Title="Copy Discord Invite",Desc="Join our Discord server",Callback=function() setclipboard("https://discord.gg/9krxH9g4MH");WindUI:Notify({Title="Copied!",Content="Discord link copied to clipboard.",Duration=5});end});end do local themes={"Dark","Light","Rose","Amethyst"};Tabs.Settings:Dropdown({Title="Select Theme",Values=themes,Multi=false,Default=1,Callback=function(Value) WindUI:SetTheme(Value);end});Tabs.Settings:Toggle({Title="Acrylic Background",Default=true,Callback=function(Value) Window:ToggleTransparency( not Value);end});Tabs.Settings:Button({Title="Toggle UI Visibility",Desc="Show/Hide the interface",Callback=function() Window.Minimized= not Window.Minimized;end});Tabs.Settings:Button({Title="Destroy UI",Desc="Close the interface completely",Callback=function() WindUI:Destroy();end});end WindUI:Notify({Title="EyeHub",Content="Script loaded successfully!",Duration=5});Window:SelectTab(1); end
